import React from "react";
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";

const Booking = ({ tour, avgRating }) => {
  const { price, reviews } = tour;
  
  const handleChange =(e)=>{

  }


  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ${price} <span>/per person</span>
        </h3>
        <span className="tour__rating d-flex align-items-center">
          <i
            class="ri-star-fill"
           
          ></i>

          {avgRating === 0 ? null : avgRating} ({reviews?.length})
          
        </span>
      </div>
    
    {/* ============== Booking Form =============== */}

    <div className="booking__form">
        <h5>
            Information
        </h5>
        <Form className="booking__infor-form">
            <FormGroup>
                <input type="text" name="" id="fullName" placeholder="Full name" required onChange={handleChange}/>
            </FormGroup>
            <FormGroup>
                <input type="number" name="" id="phone" placeholder="Phone Number" required onChange={handleChange}/>
            </FormGroup>
            <FormGroup className="d-flex align-items-center gap-3">
                <input type="date" name="" id="bookAt" placeholder="" required onChange={handleChange}/>
                <input type="number" name="" id="guestSize" placeholder="Guest" required onChange={handleChange}/>

            </FormGroup>
        </Form>
    </div>
    {/* ============== Booking Form End=============== */}



{/* =========Booking bottom============ */}
    <div className="booking__bottom">
        <ListGroup>
            <ListGroupItem className="border-0 px-0">
               <h5></h5>
            </ListGroupItem>
        </ListGroup>
    </div>
    </div>
  );
};

export default Booking;
