import React,{useState} from "react";
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";

const Booking = ({ tour, avgRating }) => {
  const { price, reviews } = tour;
  
  const [credentials,setCredentials]=useState({
    userId:'01', //Later it will be dynamic
    userEmail:'example@gmail.com',
    fullName:'',
    phone:'',
    guestSize:1,
    bookAt:''

  })
  const handleChange =(e)=>{
     setCredentials(prev=>({...prev,[e.target.id]:e.target.value}))
  };

  const serviceFee=10
  const totalAmount=Number(price)*number(credentials.guestSize) + Number(serviceFee)

   //send data to the server
   const handleClick=e=>{
    e.preventDefault();
    console.log(credentials);
   }


  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ${price} <span>/per person</span>
        </h3>
        <span className="tour__rating d-flex align-items-center">
          <i
            class="ri-star-fill"
           
          ></i>

          {avgRating === 0 ? null : avgRating} ({reviews?.length})
          
        </span>
      </div>
    
    {/* ============== Booking Form =============== */}

    <div className="booking__form">
        <h5>
            Information
        </h5>
        <Form className="booking__info-form" onClick={handleClick}>
            <FormGroup>
                <input type="text" name="" id="fullName" placeholder="Full name" required onChange={handleChange}/>
            </FormGroup>
            <FormGroup>
                <input type="number" name="" id="phone" placeholder="Phone Number" required onChange={handleChange}/>
            </FormGroup>
            <FormGroup className="d-flex align-items-center gap-3">
                <input type="date" name="" id="bookAt" placeholder="" required onChange={handleChange}/>
                <input type="number" name="" id="guestSize" placeholder="Guest" required onChange={handleChange}/>

            </FormGroup>
        </Form>
    </div>
    {/* ============== Booking Form End=============== */}



{/* =========Booking bottom============ */}
    <div className="booking__bottom">
        <ListGroup>
            <ListGroupItem className="border-0 px-0">
               <h5 className="d-flex align-items-center gap-1">${price} <i class="ri-close-line"></i> 1 person</h5>
               <span>${price}
               </span>
            </ListGroupItem>
            <ListGroupItem className="border-0 px-0">
               <h5>Service Charge</h5>
               <span>${serviceFee}
               </span>
            </ListGroupItem>
            <ListGroupItem className="border-0 px-0 total">
               <h5>Total</h5>
               <span>$109
               </span>
            </ListGroupItem>
        </ListGroup>

        <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>Book Now</Button>
    </div>
    </div>
  );
};

export default Booking;
